select.if:
	SELECT a.id, a.head, a.`name`, a.`status`, a.phone, concat(ad.province,concat(ad.city,ad.district)) as address from zf_agency a , 
	zf_address ad WHERE (a.id IN (SELECT s.anencyid FROM zf_store s WHERE s.userid = #{0} AND s.type = #{1} AND s.enabled = 0)) 
		AND ad.addressid = a.address

	 	AND a.`name` like concat('%',#{2},'%')


 		AND a.address IN (SELECT addr.addressid FROM zf_address addr WHERE addr.province=#{4}

 				AND addr.city=#{5}


 				AND addr.district=#{6}

 		)

	SELECT a.id, a.head, a.agencyid, a.`name`, a.`status`, agency.`name` as agencyname  from zf_agencyemployee a, zf_agency agency  
 	WHERE a.id IN (SELECT s.anencyid FROM zf_store s WHERE s.userid = #{0} AND s.type = #{1} AND s.enabled = 0)

	 	AND a.`name` like concat('%',#{2},'%')



			AND agency.`name` LIKE CONCAT('%',#{3},'%') 


	 		AND agency.address IN (SELECT addr.addressid FROM zf_address addr WHERE addr.province=#{4}

	 				AND addr.city=#{5}


	 				AND addr.district=#{6}

	 		)
